#include <iostream>
#include "LogFrame.hpp"

int main(int argc, const char * argv[]) {
    LogFrame frame = parse({"0000000009    Offset=3.21745,BW=22MHz,MCS=1(802.11 legacy DBPSK 1mbps (long preamble)),Size=28", "", "FCS=Fail"});
    cout << (frame.isCorrect() ? "Correct" : "Incorrect") << endl;
    return 0;
}
